def generer_nom_prenom_recent_francais():
    annee_actuelle = datetime.now().year
    age = random.randint(7, 12)
    annee_naissance = annee_actuelle - age
    mois_naissance = random.randint(1, 12)
    jour_naissance = random.randint(1, 28)
    prenom = random.choice(prenoms)
    nom = random.choice(noms)
    ville = random.choice(villes_france)
    date_naissance = datetime(annee_naissance, mois_naissance, jour_naissance).strftime("%d/%m/%Y")
    chiffres_aleatoires = ''.join(random.choices("0123456789", k=4))
    email = prenom.lower() + nom.lower() + ville[:4].lower() + chiffres_aleatoires + "@gmail.com"
    longueur = 12
    lng_fb = 15  
    caracteres = string.ascii_letters + string.digits + string.punctuation 
    mot_de_passe = ''.join(random.choice(caracteres) for _ in range(longueur))
    mdp_fb = ''.join(random.choice(caracteres) for _ in range(lng_fb))
    info = f"Prénom: {prenom}\nNom: {nom}\nVille: {ville}\nDate de naissance: {date_naissance}\nEmail: {email}\nMdp mail: {mot_de_passe}\nMdp facebook: {mdp_fb}"
    return info


def afficher_resultat():
    resultat = generer_nom_prenom_recent_francais()
    resultat_label.config(text=resultat)
  
def enregistrer_contenu():
    info = resultat_label.cget("text") 
    contenu = info
    prenom = info.split("\n")[0].split(":")[1].strip()
    nom = info.split("\n")[1].split(":")[1].strip()    
    nom_fichier = nom.lower() + "_" + prenom.lower() + ".txt" 
    nom_dossier = nom.lower() + "_" + prenom.lower() 
    dossier_principal = nom_dossier
    sous_dossiers = [f"Photos de {prenom}-{nom}","Infos_sur_le_Pedo"]
    os.mkdir(dossier_principal)
    for sous_dossier in sous_dossiers:
        chemin_sous_dossier = os.path.join(dossier_principal, sous_dossier)
        os.mkdir(chemin_sous_dossier)
    with open(os.path.join(dossier_principal, nom_fichier), "w", encoding='utf-8') as fichier:
        fichier.write(contenu)

fenetre = tk.Tk()
fenetre.geometry("450x350")
fenetre.title("Genrateur persona")

resultat_label = tk.Label(fenetre, text="", font=("Helvetica", 14))
resultat_label.pack(pady=20)

generer_bouton = tk.Button(fenetre, text="Générer", font=("Helvetica", 14), command=afficher_resultat)
generer_bouton.pack(pady=10)

enregistrer_bouton = tk.Button(fenetre, text="Enregistrer", font=("Helvetica", 14), command=enregistrer_contenu)
enregistrer_bouton.pack(pady=10)

fenetre.mainloop()
